using System.ComponentModel.Composition;
using XrmToolBox.Extensibility;
using XrmToolBox.Extensibility.Interfaces;

namespace Fic.XTB.InAppNotificationBuilder
{
    [Export(typeof(IXrmToolBoxPlugin)),
     ExportMetadata("Name", @"In-App Notification Builder"),
     ExportMetadata("Description", "Design your in-app notifications, get live preview and source code for creating."),
     ExportMetadata("SmallImageBase64", ""),
     ExportMetadata("BigImageBase64", ""),
     ExportMetadata("BackgroundColor", "Black"),
     ExportMetadata("PrimaryFontColor", "White"),
     ExportMetadata("SecondaryFontColor", "Gray")]
    public class PluginDescription : PluginBase
    {
        public override IXrmToolBoxPluginControl GetControl()
        {
            return new InAppNotificationBuilder();
        }
    }
}